    L = [[0 for k in range(nbCol(image))] for i in range(nbLig(image))]

    for i in range(nbLig(image)):
        for j in range(nbCol(image)):
            if image[i][j] &lt; seuil :
                L[i][j] = 0
            else:
                L[i][j] = 1
